INSERT INTO `sys_oper_log` VALUES (501, '诗词', 2, 'com.main.system.controller.BizPoetryController.read()', 'PUT', 1, 'admin', '研发部门', '/system/poetry/read/5', '127.0.0.1', '内网IP', '5', '{\"msg\":\"操作成功\",\"code\":200,\"data\":{\"author\":\"刘过\",\"bizPoetryCommentList\":[],\"click\":1,\"content\":\"<p>安远楼小集，侑觞歌板之姬黄其姓者，乞词于龙洲道人，为赋此《唐多令》。同柳阜之、刘去非、石民瞻、周嘉仲、陈孟参、孟容。时八月五日也。</p><p>芦叶满汀洲，寒沙带浅流。二十年重过南楼。柳下系船犹未稳，能几日，又中秋。</p><p>黄鹤断矶头，故人今在否？旧江山浑是新愁。欲买桂花同载酒，终不似，少年游</p>\",\"createBy\":\"admin\",\"createTime\":\"2024-05-16 23:41:51\",\"dynasty\":\"5\",\"dynastyText\":\"宋\",\"id\":5,\"imgList\":[\"/profile/upload/2024/05/16/QQ20221021114707_20240516234147A009.jpg\"],\"imgs\":\"/profile/upload/2024/05/16/QQ20221021114707_20240516234147A009.jpg\",\"params\":{},\"recitation\":\"/profile/upload/2024/05/16/老街北_20240516234131A007.mp3\",\"remark\":\"\",\"title\":\"唐多令·芦叶满汀洲\",\"video\":\"/profile/upload/2024/05/16/七里Smile - 幹物女_20240516234141A008.mp4\"}}', 0, NULL, '2024-05-16 23:42:00', 7);
INSERT INTO `sys_oper_log` VALUES (502, '诗词', 1, 'com.main.system.controller.BizPoetryController.add()', 'POST', 1, 'admin', '研发部门', '/system/poetry', '127.0.0.1', '内网IP', '{\"author\":\"辛弃疾\",\"bizPoetryCommentList\":[],\"click\":0,\"content\":\"<p>东风夜放花千树，更吹落、星如雨。宝马雕车香满路。凤箫声动，玉壶光转，一夜鱼龙舞。</p><p>蛾儿雪柳黄金缕，笑语盈盈暗香去。众里寻他千百度。蓦然回首，那人却在，灯火阑珊处。</p>\",\"createBy\":\"admin\",\"createTime\":\"2024-05-16 23:44:58\",\"dynasty\":\"5\",\"id\":6,\"imgs\":\"/profile/upload/2024/05/16/QQ20221021114707_20240516234456A012.jpg\",\"params\":{},\"recitation\":\"/profile/upload/2024/05/16/老街北_20240516234449A010.mp3\",\"remark\":\"\",\"title\":\"青玉案·元夕\",\"video\":\"/profile/upload/2024/05/16/七里Smile - 幹物女_20240516234452A011.mp4\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2024-05-16 23:44:58', 16);
