INSERT INTO `sys_oper_log` VALUES (510, '诗词', 1, 'com.main.system.controller.BizPoetryController.add()', 'POST', 1, 'admin', '研发部门', '/system/poetry', '127.0.0.1', '内网IP', '{\"author\":\"织田信长\",\"bizPoetryCommentList\":[],\"click\":0,\"content\":\"<p>常思人世漂流无常</p><p>譬如朝露</p><p>水中映月</p><p>刹那繁华瞬间即逝</p><p>风流人物</p><p>今非昔比</p><p>人生五十年</p><p>莫非熙熙攘攘</p><p>浮生幻梦</p><p>名垂青史</p><p>功败湮灭</p><p>只是宿命因果</p><p>一念之间</p><p>有何可惜</p><p>急至京都忧心难忍</p><p>狱门示众</p><p>敦盛之首</p><p>窃而归家传僧供奉</p><p>叹息如烟</p><p>人间无常</p><p>人生五十年</p><p>莫非熙熙攘攘</p><p>浮生幻梦</p><p>人生五十年，放眼天下，去事宛如梦幻，一度得生者，岂有常不灭？</p>\",\"createBy\":\"admin\",\"createTime\":\"2024-05-16 23:52:58\",\"dynasty\":\"11\",\"id\":10,\"imgs\":\"/profile/upload/2024/05/16/QQ20221021114707_20240516235257A024.jpg\",\"params\":{},\"recitation\":\"/profile/upload/2024/05/16/老街北_20240516235249A022.mp3\",\"remark\":\"\",\"title\":\"人间五十年\",\"video\":\"/profile/upload/2024/05/16/七里Smile - 幹物女_20240516235253A023.mp4\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2024-05-16 23:52:59', 6);
