INSERT INTO `sys_oper_log` VALUES (514, '诗词', 1, 'com.main.system.controller.BizPoetryController.add()', 'POST', 1, 'admin', '研发部门', '/system/poetry', '127.0.0.1', '内网IP', '{\"author\":\"李煜\",\"bizPoetryCommentList\":[],\"click\":0,\"content\":\"<p>多少恨，昨夜梦魂中。还似旧时游上苑，车如流水马如龙。花月正春风。</p>\",\"createBy\":\"admin\",\"createTime\":\"2024-05-16 23:56:01\",\"dynasty\":\"5\",\"id\":12,\"imgs\":\"/profile/upload/2024/05/16/QQ20221021114707_20240516235557A029.jpg\",\"params\":{},\"recitation\":\"/profile/upload/2024/05/16/老街北_20240516235549A028.mp3\",\"remark\":\"\",\"title\":\"望江南·多少恨\",\"video\":\"/profile/upload/2024/05/16/七里Smile - 幹物女_20240516235600A030.mp4\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2024-05-16 23:56:01', 16);
INSERT INTO `sys_oper_log` VALUES (515, '诗词', 1, 'com.main.system.controller.BizPoetryController.add()', 'POST', 1, 'admin', '研发部门', '/system/poetry', '127.0.0.1', '内网IP', '{\"author\":\"林逋\",\"bizPoetryCommentList\":[],\"click\":0,\"content\":\"<p>众芳摇落独暄妍，占尽风情向小园。</p><p>疏影横斜水清浅，暗香浮动月黄昏。</p><p>霜禽欲下先偷眼，粉蝶如知合断魂。</p><p>幸有微吟可相狎，不须檀板共金樽。</p><p><br></p><p>剪绡零碎点酥乾，向背稀稠画亦难。</p><p>日薄从甘春至晚，霜深应怯夜来寒。</p><p>澄鲜只共邻僧惜，冷落犹嫌俗客看。</p><p>忆着江南旧行路，酒旗斜拂堕吟鞍。</p>\",\"createBy\":\"admin\",\"createTime\":\"2024-05-16 23:57:39\",\"dynasty\":\"5\",\"id\":13,\"imgs\":\"/profile/upload/2024/05/16/QQ20221021114707_20240516235737A033.jpg\",\"params\":{},\"recitation\":\"/profile/upload/2024/05/16/老街北_20240516235732A031.mp3\",\"remark\":\"\",\"title\":\"山园小梅二首\",\"video\":\"/profile/upload/2024/05/16/七里Smile - 幹物女_20240516235734A032.mp4\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2024-05-16 23:57:39', 16);
