INSERT INTO `sys_oper_log` VALUES (541, '诗词', 1, 'com.main.system.controller.BizPoetryController.add()', 'POST', 1, 'admin', '研发部门', '/system/poetry', '127.0.0.1', '内网IP', '{\"author\":\"晏几道\",\"bizPoetryCommentList\":[],\"click\":0,\"content\":\"<p>梦后楼台高锁，酒醒帘幕低垂。去年春恨却来时。落花人独立，微雨燕双飞。</p><p>记得小蘋初见，两重心字罗衣。琵琶弦上说相思。当时明月在，曾照彩云归。</p>\",\"createBy\":\"admin\",\"createTime\":\"2024-05-17 00:19:44\",\"dynasty\":\"5\",\"id\":27,\"imgs\":\"\",\"params\":{},\"remark\":\"\",\"title\":\"临江仙·梦后楼台高锁\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2024-05-17 00:19:44', 15);
INSERT INTO `sys_oper_log` VALUES (542, '诗词', 2, 'com.main.system.controller.BizPoetryController.read()', 'PUT', 1, 'admin', '研发部门', '/system/poetry/read/27', '127.0.0.1', '内网IP', '27', '{\"msg\":\"操作成功\",\"code\":200,\"data\":{\"author\":\"晏几道\",\"bizPoetryCommentList\":[],\"click\":1,\"content\":\"<p>梦后楼台高锁，酒醒帘幕低垂。去年春恨却来时。落花人独立，微雨燕双飞。</p><p>记得小蘋初见，两重心字罗衣。琵琶弦上说相思。当时明月在，曾照彩云归。</p>\",\"createBy\":\"admin\",\"createTime\":\"2024-05-17 00:19:45\",\"dynasty\":\"5\",\"dynastyText\":\"宋\",\"id\":27,\"imgList\":[\"\"],\"params\":{},\"remark\":\"\",\"title\":\"临江仙·梦后楼台高锁\"}}', 0, NULL, '2024-05-17 00:19:46', 10);
INSERT INTO `sys_oper_log` VALUES (543, '诗词', 1, 'com.main.system.controller.BizPoetryController.add()', 'POST', 1, 'admin', '研发部门', '/system/poetry', '127.0.0.1', '内网IP', '{\"author\":\"元稹\",\"bizPoetryCommentList\":[],\"click\":0,\"content\":\"<p>曾经沧海难为水，除却巫山不是云。</p><p>取次花丛懒回顾，半缘修道半缘君。</p>\",\"createBy\":\"admin\",\"createTime\":\"2024-05-17 00:20:26\",\"dynasty\":\"4\",\"id\":28,\"imgs\":\"\",\"params\":{},\"remark\":\"\",\"title\":\"离思五首·其四\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2024-05-17 00:20:26', 14);
INSERT INTO `sys_oper_log` VALUES (544, '诗词', 2, 'com.main.system.controller.BizPoetryController.read()', 'PUT', 1, 'admin', '研发部门', '/system/poetry/read/28', '127.0.0.1', '内网IP', '28', '{\"msg\":\"操作成功\",\"code\":200,\"data\":{\"author\":\"元稹\",\"bizPoetryCommentList\":[],\"click\":1,\"content\":\"<p>曾经沧海难为水，除却巫山不是云。</p><p>取次花丛懒回顾，半缘修道半缘君。</p>\",\"createBy\":\"admin\",\"createTime\":\"2024-05-17 00:20:26\",\"dynasty\":\"4\",\"dynastyText\":\"唐\",\"id\":28,\"imgList\":[\"\"],\"params\":{},\"remark\":\"\",\"title\":\"离思五首·其四\"}}', 0, NULL, '2024-05-17 00:20:30', 17);
INSERT INTO `sys_oper_log` VALUES (545, '诗词', 1, 'com.main.system.controller.BizPoetryController.add()', 'POST', 1, 'admin', '研发部门', '/system/poetry', '127.0.0.1', '内网IP', '{\"author\":\"苏轼\",\"bizPoetryCommentList\":[],\"click\":0,\"content\":\"<p>落日绣帘卷，亭下水连空。知君为我新作，窗户湿青红。长记平山堂上，欹枕江南烟雨，杳杳没孤鸿。认得醉翁语，山色有无中。</p><p>一千顷，都镜净，倒碧峰。忽然浪起，掀舞一叶白头翁。堪笑兰台公子，未解庄生天籁，刚道有雌雄。一点浩然气，千里快哉风。</p>\",\"createBy\":\"admin\",\"createTime\":\"2024-05-17 00:21:01\",\"dynasty\":\"5\",\"id\":29,\"imgs\":\"\",\"params\":{},\"remark\":\"\",\"title\":\"水调歌头·黄州快哉亭赠张偓佺\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2024-05-17 00:21:01', 16);
INSERT INTO `sys_oper_log` VALUES (546, '诗词', 1, 'com.main.system.controller.BizPoetryController.add()', 'POST', 1, 'admin', '研发部门', '/system/poetry', '127.0.0.1', '内网IP', '{\"author\":\"欧阳修\",\"bizPoetryCommentList\":[],\"click\":0,\"content\":\"<p>尊前拟把归期说，欲语春容先惨咽。人生自是有情痴，此恨不关风与月。</p><p>离歌且莫翻新阕，一曲能教肠寸结。直须看尽洛城花，始共春风容易别。</p>\",\"createBy\":\"admin\",\"createTime\":\"2024-05-17 00:21:36\",\"dynasty\":\"5\",\"id\":30,\"imgs\":\"\",\"params\":{},\"remark\":\"\",\"title\":\"玉楼春·尊前拟把归期说\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2024-05-17 00:21:36', 6);
INSERT INTO `sys_oper_log` VALUES (547, '诗词', 1, 'com.main.system.controller.BizPoetryController.add()', 'POST', 1, 'admin', '研发部门', '/system/poetry', '127.0.0.1', '内网IP', '{\"author\":\"毛泽东\",\"bizPoetryCommentList\":[],\"click\":0,\"content\":\"<p>独坐池塘如虎踞，绿荫树下养精神。</p><p>春来我不先开口，哪个虫儿敢作声？</p>\",\"createBy\":\"admin\",\"createTime\":\"2024-05-17 00:23:06\",\"dynasty\":\"10\",\"id\":31,\"imgs\":\"\",\"params\":{},\"remark\":\"\",\"title\":\"七古咏蛙\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2024-05-17 00:23:06', 16);
INSERT INTO `sys_oper_log` VALUES (548, '诗词', 1, 'com.main.system.controller.BizPoetryController.add()', 'POST', 1, 'admin', '研发部门', '/system/poetry', '127.0.0.1', '内网IP', '{\"author\":\"毛泽东\",\"bizPoetryCommentList\":[],\"click\":0,\"content\":\"<p>西风烈，长空雁叫霜晨月。霜晨月，马蹄声碎，喇叭声咽。</p><p>雄关漫道真如铁，而今迈步从头越。从头越，苍山如海，残阳如血。</p>\",\"createBy\":\"admin\",\"createTime\":\"2024-05-17 00:23:55\",\"dynasty\":\"10\",\"id\":32,\"imgs\":\"\",\"params\":{},\"remark\":\"\",\"title\":\"忆秦娥·娄山关\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2024-05-17 00:23:55', 15);
INSERT INTO `sys_oper_log` VALUES (549, '诗词', 1, 'com.main.system.controller.BizPoetryController.add()', 'POST', 1, 'admin', '研发部门', '/system/poetry', '127.0.0.1', '内网IP', '{\"author\":\"陈毅\",\"bizPoetryCommentList\":[],\"click\":0,\"content\":\"<p>一九三六年冬，梅山被围。余伤病伏丛莽间二十余日，虑不得脱，得诗三首留衣底。旋围解。</p><p><br></p><p>断头今日意如何？创业艰难百战多。</p><p>此去泉台招旧部，旌旗十万斩阎罗。</p><p><br></p><p>南国烽烟正十年，此头须向国门悬。</p><p>后死诸君多努力，捷报飞来当纸钱。</p><p><br></p><p>投身革命即为家，血雨腥风应有涯。</p><p>取义成仁今日事，人间遍种自由花。</p>\",\"createBy\":\"admin\",\"createTime\":\"2024-05-17 00:24:45\",\"dynasty\":\"10\",\"id\":33,\"imgs\":\"\",\"params\":{},\"remark\":\"\",\"title\":\"梅岭三章\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2024-05-17 00:24:45', 15);
INSERT INTO `sys_oper_log` VALUES (550, '诗词', 2, 'com.main.system.controller.BizPoetryController.read()', 'PUT', 1, 'admin', '研发部门', '/system/poetry/read/33', '127.0.0.1', '内网IP', '33', '{\"msg\":\"操作成功\",\"code\":200,\"data\":{\"author\":\"陈毅\",\"bizPoetryCommentList\":[],\"click\":1,\"content\":\"<p>一九三六年冬，梅山被围。余伤病伏丛莽间二十余日，虑不得脱，得诗三首留衣底。旋围解。</p><p><br></p><p>断头今日意如何？创业艰难百战多。</p><p>此去泉台招旧部，旌旗十万斩阎罗。</p><p><br></p><p>南国烽烟正十年，此头须向国门悬。</p><p>后死诸君多努力，捷报飞来当纸钱。</p><p><br></p><p>投身革命即为家，血雨腥风应有涯。</p><p>取义成仁今日事，人间遍种自由花。</p>\",\"createBy\":\"admin\",\"createTime\":\"2024-05-17 00:24:45\",\"dynasty\":\"10\",\"dynastyText\":\"现代\",\"id\":33,\"imgList\":[\"\"],\"params\":{},\"remark\":\"\",\"title\":\"梅岭三章\"}}', 0, NULL, '2024-05-17 00:24:46', 10);
